import React, { useState } from 'react';

function ProjectList() {
    const [openProject, setOpenProject] = useState(null);

    const project = [
        { id: 1, title: 'City Brew and Bites', description: 'This is a fictional website made for the CMS course at FH Salzburg', image: './images/cafe.png' },
        { id: 2, title: 'Consumer Score', description: 'An assignment from the Machine Learning Course, regarding consumer behaviour', image: './images/ml.png' },
        { id: 3, title: 'Funny Gnome Drawing', description: 'This is a recent drawing of a gnome I made. He is a jolly fella', image: './images/gnome.png' },
    ];

    const URL = 'http://localhost:3000/projects';

useEffect(() => {
        fetch(URL)
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then((data) => setProjects(data))
            .catch((err) => setError(err.message));
    }, []); // Empty dependency array ensures this runs only once


    return (
        <div>
            <br></br>
            <div className="projects">
                {projects.map((project) => (
                    <div key={project.id} className="project">
                        <h2>{project.title}</h2>
                        <button onClick={() => toggleProjectDetails(project.id)}>
                            {openProject === project.id ? 'Hide Details' : 'Show Details'}
                        </button>
                        {openProject === project.id && (
                            <div>
                                <p>{project.description}</p>
                                <img src={project.image} alt={project.title} className='project-image' />
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProjectList;
