import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const ContactForm = () => {
    const validationSchema = Yup.object({
        email: Yup.string()
            .required('Feld ist verpflichtend')
            .email('Die von Ihnen gew채hlte Email ist nicht valide'),
        message: Yup.string()
            .required('Feld ist verpflichtend')
            .min(10, 'Nachrichten m체ssen mindestens 10 Zeichen lang sein'),
    });

    const initialValues = {
        name: '',
        email: '',
        message: '',
    };

    const handleSubmit = (values, { resetForm }) => {
        console.log('Form data:', values);
        alert('Ihre Nachricht wurde versendet!');
        resetForm();
    };

    return (
        <div className='form'>
            <Formik
                initialValues={initialValues}
                validationSchema={validationSchema}
                onSubmit={handleSubmit}
            >
                {() => (
                    <Form>
                        <div className='name-form'>
                            <br></br>
                            <label htmlFor="name">Name</label><br></br>
                            <Field name="name" type="text" placeholder="Ihr Name" />
                        </div>

                        <div className='email-form'>
                            <label htmlFor="email">Email*</label><br></br>
                            <Field name="email" type="email" placeholder="Ihre Email" />
                            <ErrorMessage name="email" component="div" style={{ color: 'hotpink', fontSize: '0.8rem' }} />
                        </div>

                        <div className='message-form'>
                            <label htmlFor="message">Nachricht*</label><br></br>
                            <Field
                                name="message"
                                as="textarea"
                                placeholder="Ihre Message"
                                rows="5"
                            />
                            <ErrorMessage name="message" component="div" style={{ color: 'red', fontSize: '0.8rem' }} />
                        </div>

                        <button type="submit" style={{ marginTop: '1rem' }}>
                            Abschicken
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    );
};

export default ContactForm;
